mapper.select:
select t_item_procurement.*,t_item_type.name as
itemTypeName,short_name as supplierName,t_staff.staff_name as staffName,t_measure_unit.name as measureUnitName FROM t_item_procurement,t_item_type,t_staff,t_supplier,t_measure_unit where is_in_storage=0 and t_item_type.name= (select name from t_item_type where id=item_type_id) and short_name=(select short_name from t_supplier where id=supplier_id)and t_staff.staff_name=(select staff_name from t_staff
where id=staff_id)and t_measure_unit.name=(select name from t_measure_unit where id=measure_unit_id)
and supplier_id=#{supplierId}
and staff_id=#{staffId}
and item_type_id=#{itemTypeId}
and item_name like CONCAT('%',#{itemName},'%')
order by time desc
order by time
limit #{start},#{end}

select count(*)


	and supplier_id=#{supplierId}


	and staff_id=#{staffId}


	and item_type_id=#{itemTypeId}


	and item_name=#{itemName}

select t_item_procurement.*,t_item_type.name as itemTypeName,short_name as supplierName,t_staff.staff_name as staffName,t_measure_unit.name as measureUnitName from
t_item_procurement,t_item_type,t_staff,t_supplier,t_measure_unit where is_in_storage=0 and t_item_type.name= (select name from t_item_type where id=item_type_id) and short_name=(select short_name from t_supplier
where id=supplier_id)and t_staff.staff_name=(select staff_name from t_staff
where id=staff_id)and t_measure_unit.name=(select name from t_measure_unit where id=measure_unit_id) and end_time is not null
and supplier_id=#{supplierId}
and staff_id=#{staffId}
and item_type_id=#{itemTypeId}
and item_name=#{itemName}
order by end_time desc
order by end_time
limit #{start},#{end}

select count(*)


	and supplier_id=#{supplierId}


	and staff_id=#{staffId}


	and item_type_id=#{itemTypeId}


	and item_name=#{itemName}
